import sqlite3
import hashlib
import sys

# ===============================
# Database Setup
# ===============================
DB_NAME = "school.db"

def get_connection():
    return sqlite3.connect(DB_NAME)

def setup_database():
    with get_connection() as conn:
        cursor = conn.cursor()
        cursor.execute("""
        CREATE TABLE IF NOT EXISTS users (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            username TEXT UNIQUE NOT NULL,
            password TEXT NOT NULL
        )
        """)
        conn.commit()

# ===============================
# Utility Functions
# ===============================
def hash_password(password: str) -> str:
    """Return SHA-256 hashed password."""
    return hashlib.sha256(password.encode()).hexdigest()

# ===============================
# Authentication Module
# ===============================
class AuthSystem:

    @staticmethod
    def register():
        username = input("ğŸ‘¤ Enter username: ").strip()
        password = input("ğŸ”‘ Enter password: ").strip()
        confirm_password = input("ğŸ”‘ Confirm password: ").strip()

        if password != confirm_password:
            print("âŒ Passwords do not match.")
            return

        with get_connection() as conn:
            cursor = conn.cursor()
            try:
                cursor.execute("INSERT INTO users (username, password) VALUES (?, ?)",
                               (username, hash_password(password)))
                conn.commit()
                print("âœ… Registration successful!")
            except sqlite3.IntegrityError:
                print("âŒ Username already exists. Try another.")

    @staticmethod
    def login():
        username = input("ğŸ‘¤ Enter username: ").strip()
        password = input("ğŸ”‘ Enter password: ").strip()

        with get_connection() as conn:
            cursor = conn.cursor()
            cursor.execute("SELECT password FROM users WHERE username = ?", (username,))
            result = cursor.fetchone()

            if result and result[0] == hash_password(password):
                print(f"âœ… Welcome {username}, login successful!")
            elif result:
                print("âŒ Incorrect password.")
            else:
                print("âŒ User not found. Please register first.")

# ===============================
# Main Application
# ===============================
def main_menu():
    while True:
        print("\n" + "="*40)
        print(" ğŸ“ School / Academy Management System ")
        print("="*40)
        print("1ï¸âƒ£  Register")
        print("2ï¸âƒ£  Login")
        print("3ï¸âƒ£  Exit")
        print("="*40)

        choice = input("ğŸ‘‰ Enter your choice: ").strip()

        if choice == "1":
            AuthSystem.register()
        elif choice == "2":
            AuthSystem.login()
        elif choice == "3":
            print("ğŸ‘‹ Exiting system. Goodbye!")
            return
        else:
            print("âŒ Invalid choice. Please try again.")

# ===============================
# Run Program
# ===============================
if __name__ == "__main__":
    setup_database()
    main_menu()